mapper.select:

select 

from shoucang
where UserName = #{username,jdbcType=VARCHAR}
  and TieZiId = #{tieziid,jdbcType=INTEGER}

mapper.delete:

delete from shoucang
where UserName = #{username,jdbcType=VARCHAR}
  and TieZiId = #{tieziid,jdbcType=INTEGER}

mapper.update:

update shoucang
set ShouCangTime = #{shoucangtime,jdbcType=TIMESTAMP},
  ZuoZhePetName = #{zuozhepetname,jdbcType=VARCHAR},
  BiaoQian = #{biaoqian,jdbcType=VARCHAR},
  UpdateState = #{updatestate,jdbcType=INTEGER},
  UpdateTime = #{updatetime,jdbcType=TIMESTAMP}
where UserName = #{username,jdbcType=VARCHAR}
  and TieZiId = #{tieziid,jdbcType=INTEGER}
